
const sqlShowTables = `SELECT *
FROM pg_catalog.pg_tables
WHERE schemaname != 'pg_catalog'
  AND schemaname != 'information_schema';`;

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

function makeResultDisplay (sql, result) {
    let d = document.body
        .firstElementChild
        .nextElementSibling
        .insertAdjacentElement("afterend",document.createElement("div"));
    let header = d.appendChild(document.createElement("div"));
    header.classList.add("header");
    header.textContent = new Date();
    d.appendChild(document.createElement("pre")).textContent = sql;
    d.querySelector("pre").setAttribute("data-language", "sql");

    if (result.dberror !== undefined) {
        let e = d.appendChild(document.createElement("pre"));
        e.textContent = JSON.stringify(result.dberror, null, 2);
        e.classList.add("error");
        return;
    }

    let table = d.appendChild(document.createElement("table"));

    if (result.fields !== undefined) {
        let tr = table.appendChild(document.createElement("tr"))
        result.fields.forEach(cell => {
            let th = tr.appendChild(document.createElement("th"));
            th.textContent = cell.name;
            th.setAttribute("data-typesize", cell.dataTypeSize);
        });
    }

    result.rows.forEach(row => {
        let r = table.appendChild(document.createElement("tr"));
        Object.keys(row).forEach(cell => {
            let c = r.appendChild(document.createElement("td"));
            c.appendChild(document.createElement("pre")).textContent = row[cell];
        });
    });
};

async function sendSql() {
    let ta = document.querySelector("div.sqlarea");
    let sql = ta.textContent;
    if (sql == "") {
        return;
    }
    let form = new FormData();
    form.append("command", sql);
    let response = await fetch(document.location, {
        method: "POST",
        body: form
    });
    if (response.status != 200) {
        console.log("sendSql did not succeed", response.status, response);
        return;
    }
    let result = await response.json();
    ta.textContent = "";

    console.log("sql result", result);

    makeResultDisplay(sql, result);
}

var historyPointer = 0;

function initSqlArea() {
    let area = document.querySelector("div.sqlarea");
    area.addEventListener("keypress", keyEvt => {
        if (keyEvt.keyCode == 10 && keyEvt.ctrlKey) {
            sendSql();
            keyEvt.preventDefault();
        }
    });

    area.addEventListener("keydown", keyDown => {
        let divs = document.querySelectorAll("div");
        // console.log("keyEvt", keyDown);

        if (keyDown.key == "ArrowUp") {
            if (historyPointer >= divs.length - 1) {
                beep();
            }
            else {
                historyPointer++;
                let content = divs[historyPointer]
                    .querySelector("pre")
                    .textContent;
                area.textContent = content;
            }
            keyDown.preventDefault();
        }
        if (keyDown.key == "ArrowDown") {
            if (historyPointer == 0) {
                area.textContent = "";
            }
            else {
                historyPointer--;
                let content = divs[historyPointer]
                    .querySelector("pre")
                    .textContent;
                area.textContent = content;
            }
            keyDown.preventDefault();
        }
        if (keyDown.key == "t" && keyDown.altKey) {
            historyPointer = 0;
            area.textContent = sqlShowTables;
            keyDown.preventDefault();
        }
    });

    area.setAttribute("data-language", "sql");
    area.focus();
}

const es = new EventSource("results");

es.addEventListener("meta", metaEvt => {
    console.log("meta", metaEvt);
});

es.addEventListener("result", resultEvt => {
    try {
        console.log("result", resultEvt);
    }
    catch (e) {
        console.log("error while handling resultEvt", e);
    }
});


window.addEventListener("load", evt => {
    initSqlArea();
});

